// src/pages/Characters.js

import { useState, useEffect } from "react";
import axios from "axios";
import Table from "./Table";

const API_URL = " https://rickandmortyapi.com";

function Characters() {
  const [characters, setCharacters] = useState([]);

  const getAllCharacters = () => {
    axios
      .get(`${API_URL}/api/character`)
      .then((response) => {
        console.log("characters", response.data.result);
        setCharacters(response.data.result);
      })
      .catch((error) => console.log(error));
  };

  useEffect(() => {
    getAllCharacters();
  }, []); // so code doesn't run infinitely

  return (
    <div>
      <h3>List of characters</h3>

      {characters.map((char) => (
        <div key={char._id} className="card">
          <img src={char.img} alt="apartment" />
          <h3>{char.title}</h3>
          <p>Price: {char.pricePerDay}</p>
        </div>
      ))}
    </div>
  );
}

export default Characters;
