// src/pages/Characters.js

import { useState, useEffect } from "react";
import axios from "axios";
import Table from "./table";

const API_URL = " https://rickandmortyapi.com";

function RickAndMortyTable() {
  const [characters, setCharacter] = useState([]);
  /* const [locations, setLocations] = useState([]);
  const [episodes, setEpisodes] = useState([]); */

  const getAllCharacters = () => {
    axios
      .get(`${API_URL}/api/character/?page=19`)
      .then((response) => {
          console.log('characters' , response.data);
          const allCharacters = response.data.results;
          setCharacter(allCharacters);
      })
      .catch((error) => console.log(error));
  };

  useEffect(() => {
    getAllCharacters();
  }, []); // so code doesn't run infinitely

  return (
    <Table characters={characters}/>
  );
}

export default Characters;
